function changeLanguage(lang) {
  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    if (translations[lang] && translations[lang][key]) {
      el.innerHTML = translations[lang][key];
    }
    el.style.visibility = 'visible'; // робимо елемент видимим
  });

  document.querySelectorAll('[data-i18n-title]').forEach(el => {
    const key = el.getAttribute('data-i18n-title');
    if (translations[lang] && translations[lang][key]) {
      el.setAttribute('title', translations[lang][key]);
    }
  });

  // Визначення правильного ключа заголовка сторінки
  const pathname = window.location.pathname;
  let pageTitleKey = "pageTitleIndex";

  if (pathname.includes("About-the-cartoon")) pageTitleKey = "pageTitleCartoon";
  else if (pathname.includes("Characters")) pageTitleKey = "pageTitleCharacters";
  else if (pathname.includes("Concept")) pageTitleKey = "pageTitleConcept";
  else if (pathname.includes("Comic")) pageTitleKey = "pageTitleComic";
  else if (pathname.includes("Machine-Secret")) pageTitleKey = "pageTitleMachineSecret";
  else if (pathname.includes("Documentation")) pageTitleKey = "pageTitleDocumentation";
  else if (pathname.includes("The-State")) pageTitleKey = "pageTitleState";
  else if (pathname.includes("Enemies-and-Antagonists")) pageTitleKey = "pageTitleEnemies"; 
  else if (pathname.includes("Creators-and-Allies")) pageTitleKey = "pageTitleCreators";
  else if (pathname.includes("Creators-and-Developer")) pageTitleKey = "pageTitleCreatorsDeveloper";

  // Оновлення заголовка
  document.title = translations[lang][pageTitleKey];

  localStorage.setItem('language', lang);
}

// Застосовуємо мову збережену в localStorage або за замовчуванням
document.addEventListener('DOMContentLoaded', () => {
  const savedLang = localStorage.getItem('language') || 'en';
  changeLanguage(savedLang);
});

// Обробник кнопок
document.getElementById('languageSwitcher').addEventListener('click', event => {
  if (event.target.tagName === 'BUTTON') {
    const lang = event.target.getAttribute('data-lang');
    changeLanguage(lang);
  }
});